activations.map:
function (doc) {
  var PATHSEP = "/";
  var isActivation = function (doc) { return (doc.activationId !== undefined) };
  var summarize = function (doc) {
    var endtime = doc.end !== 0 ? doc.end : undefined;
    return {
        namespace: doc.namespace,
        name: doc.name,
        version: doc.version,
        publish: doc.publish,
        annotations: doc.annotations,
        activationId: doc.activationId,
        start: doc.start,
        end: endtime,
        duration: endtime !== undefined ? doc.start - endtime : undefined,
        cause: doc.cause,
        statusCode: (doc.response !== undefined && doc.response.statusCode !== undefined) ? doc.response.statusCode : undefined
      }
  };

  if (isActivation(doc)) try {
    var date = new Date(doc.start || doc.updated);
    emit(["all"+PATHSEP+doc.namespace, date.getTime()], summarize(doc));
    emit([doc.namespace, date.getTime()], summarize(doc));
    emit([doc.namespace+PATHSEP+doc.name, date.getTime()], summarize(doc));
  } catch (e) {}
}

