entities.map:
function (doc) {
  var PATHSEP = "/";

  var isPackage = function (doc) {  return (doc.binding !== undefined) };
  var isAction = function (doc) { return (doc.exec !== undefined) };
  var isTrigger = function (doc) { return (doc.exec === undefined && doc.binding === undefined && doc.parameters !== undefined) };
  var isRule = function (doc) {  return (doc.trigger !== undefined) };

  var collection = function (doc) {
    if (isPackage(doc)) return "packages";
    if (isAction(doc)) return "actions";
    if (isTrigger(doc)) return "triggers";
    if (isRule(doc)) return "rules";
    return "??";
  };

  try {
    var ns = doc.namespace.split(PATHSEP);
    var root = ns[0]; ns.shift();
    var type = collection(doc);
    var date = new Date(doc.start || doc.updated);
    var value = {
      namespace: doc.namespace, 
      name: doc.name, 
      version: doc.version, 
      publish: doc.publish, 
      annotations: doc.annotations
    };

    if (isPackage(doc)) {
      value.binding = Object.keys(doc.binding).length !== 0;
    }

    if (type != "??") {
      // these keys are to query entities specific to a collection in a namespace
      emit([type+PATHSEP+doc.namespace, date.getTime()], value);
      if (root !== doc.namespace) {
        emit([type+PATHSEP+root, date.getTime()], value);
      }
    }

    // this key is to query all entities in a namespace    
    value.collection = type;
    emit(["all"+PATHSEP+root, date.getTime()], value);
  } catch (e) {}
}
